import React from 'react';
import './App.css'; // Make sure to import your CSS file
import 'Hover.css'
const HoverImage = () => {
  return (
    <div className="bg-sky-500 relative hover-image w-72 h-48">
      <h4 className="absolute bottom-0 right-0 text-white rounded-l-full bg-red-800 text-xs p-2 font-bold">
        @Absolute child
      </h4>
    </div>
  );
};

export default HoverImage;
